Complex.cpp<pre><code>#include &lt;iostream&gt;

class Complex {
public:
    // 构造函数
    Complex(double real = 0.0, double imag = 0.0) : real_(real), imag_(imag) {}
    // 重载 "+" 运算符(成员函数)
    Complex operator+(const Complex&amp; other) const {
        return Complex(this-&gt;real_ + other.real_, this-&gt;imag_ + other.imag_);
    }
    // 重载 "&lt;&lt;" 运算符用于输出
    friend std::ostream&amp; operator&lt;&lt;(std::ostream&amp; os, const Complex&amp; c) {
        os &lt;&lt; "(" &lt;&lt; c.real_;
        if (c.imag_ &gt;= 0) os &lt;&lt; " + " &lt;&lt; c.imag_ &lt;&lt; "i)";
        else os &lt;&lt; " - " &lt;&lt; -c.imag_ &lt;&lt; "i)";
        return os;
    }
private:
    double real_;
    double imag_;
};

int main() {
    Complex c1(3.0, 2.0);
    Complex c2(1.5, -2.5);
    Complex c3 = c1 + c2;
    std::cout &lt;&lt; "c1 + c2 = " &lt;&lt; c3 &lt;&lt; std::endl;
    return 0;
}
</code></pre> <p>更多内容请见：https://gitbookcpp.llfc.club/sections/cpp/base/cppbase30.html</p>
